<?phpheader("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Methods", "POST,OPTIONS,GET");header("Content-Type:text/html;charset=utf-8");//获得需要获取数据的参数值type$type=$_GET['type'];//对获得的参数值进行筛选，根据不同的参数值赋予不同的url链接switch ($type){case 'home':  $url = 'http://api.bilibili.com/x/web-show/res/loc?jsonp=jsonp&pf=7&id=1695';  break;case 'channel':  $url = 'http://www.bilibili.com/index/ranking-3day.json';  break;default:  break;}$ch = curl_init();curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);curl_setopt($ch , CURLOPT_URL , $url);$res = curl_exec($ch);$result = json_decode($res);echo $res;?>