<?phpheader("Access-Control-Allow-Origin: *");header("Access-Control-Allow-Methods", "POST,OPTIONS,GET");header("Content-Type:text/html;charset=utf-8");//获得需要获取数据的参数值type$type=$_GET['type'];//对获得的参数值进行筛选，根据不同的参数值赋予不同的url链接switch ($type){case 'homeBanner':    $url = 'http://api.bilibili.com/x/web-show/res/loc?jsonp=jsonp&pf=7&id=1695';    break;case 'homeHot':    $url = 'http://www.bilibili.com/index/ranking-3day.json';    break;case 'homeDing':    $url = 'http://m.bilibili.com/index/ding.html';    break;case 'homeSearch':	$url = 'http://www.bilibili.com/search?action=hotword&main_ver=v1';    break;case 'live':    $url = 'http://api.live.bilibili.com/AppIndex/home?device=phone&platform=ios&scale=3&build=10000';    break;default:    $url = 'http://m.bilibili.com/rank/'.$type.'.json';    break;}$ch = curl_init();curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);curl_setopt($ch , CURLOPT_URL , $url);$res = curl_exec($ch);$result = json_decode($res);echo $res;?>